import React,{useState,useEffect} from 'react'
import arrow from "./images/icon-arrow.svg"
import background from './images/pattern-bg-desktop.png'

function Container() {
    const[ipaddress, setIpaddress] =  useState(null)
  const[map,setMap] = useState("")

  const checkIpAddress = /^(([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])\.){3}([0-9]|[1-9][0-9]|1[0-9]{2}|2[0-4][0-9]|25[0-5])$/gi
  const checkDomain = /^[a-zA-Z0-9][a-zA-Z0-9-]{1,61}[a-zA-Z0-9](?:\.[a-zA-Z]{2,})+/

  const fetchIP = async () => {
    const res = await fetch(' https://geo.ipify.org/api/v2/country?apiKey=at_85GmLmyV1D8YXWFbggczecdHtwjVw&ipAddress=8.8.8.8')
    const data = await res.json()
    // console.log(data)
    setIpaddress(data)
  }
  useEffect(() =>{
    try{
      fetchIP()
    }
    catch(error){
      console.trace(error)
    }
  },[])

  const getIPaddress = async () =>{
    const res = await fetch(`https://geo.ipify.org/api/v2/country,city?apiKey=${process.env.API_KEY }&${
      checkIpAddress.test(map)
        ? `ipAddress=${map}`
        : checkDomain.test(map)
        ? `domain=${map}`
        : ""
    }`)
    const data = await res.json()
    // console.log(data)
    setIpaddress(data)
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    getIPaddress()
    setIpaddress("")
  }

  return (
    <>
    <section>
        <div className='absolute w-full -z-10'>
        <img src={background} alt="" className="w-full h-80" />
        </div>
        <div className="max-w-xl mx-auto p-8">
          <h1 className="font-bold text-2xl lg:text-3xl text-white pb-8 text-center">
            IP Address Tracker
          </h1>
            <form onSubmit={handleSubmit} className="w-full flex" autoComplete='off'>
               <input type="text" placeholder='Search for any IP address or domain' 
                className="w-full py-2 px-4 rounded-l-lg"
                value={map}
                onChange={(e) => setMap(e.target.value)}
               />
         <button type="submit" className="bg-black py-2 px-4 rounded-r-lg">
              <img src={arrow} alt="" /> </button> 
            </form></div>
    {ipaddress && (
        <>
        </>
    )}
        <article>

        </article>
    </section>
    </>
  )
}

export default Container
